import React, { useEffect, useState } from 'react';
import { Line } from 'react-chartjs-2';

const Dashboard = () => {
    const [users, setUsers] = useState([]);
    const [disasters, setDisasters] = useState([]);
    const [predictions, setPredictions] = useState([]);
    const [resources, setResources] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchData = async () => {
            setLoading(true);
            try {
                const responses = await Promise.all([
                    fetch('http://localhost:5000/api/users'),
                    fetch('http://localhost:5000/api/predictions'),
                    fetch('http://localhost:5000/api/resources'),
                ]);

                const [userResponse, disasterResponse, predictionResponse, resourceResponse] = responses;

                if (!userResponse.ok) throw new Error('Failed to fetch users');
                if (!disasterResponse.ok) throw new Error('Failed to fetch disasters');
                if (!predictionResponse.ok) throw new Error('Failed to fetch predictions');
                if (!resourceResponse.ok) throw new Error('Failed to fetch resources');

                const [userData, disasterData, predictionData, resourceData] = await Promise.all([
                    userResponse.json(),
                    disasterResponse.json(),
                    predictionResponse.json(),
                    resourceResponse.json(),
                ]);

                setUsers(userData || []);
                setDisasters(disasterData || []);
                setPredictions(predictionData || []);
                setResources(resourceData || []);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error}</div>;

    // Prepare data for the line chart
    const disasterCounts = disasters.reduce((acc, disaster) => {
        const date = new Date(disaster.date).toLocaleDateString();
        acc[date] = (acc[date] || 0) + 1;
        return acc;
    }, {});

    const labels = Object.keys(disasterCounts);
    const data = Object.values(disasterCounts);

    return (
        <div className="container mx-auto p-4">
            <h1 className="text-3xl font-bold mb-6">Dashboard</h1>

            <section className="mb-6">
                <h2 className="text-2xl font-semibold mb-4">Users</h2>
                <div className="overflow-x-auto bg-white border border-gray-300 rounded-lg">
                    <table className="min-w-full">
                        <thead>
                            <tr className="bg-gray-200">
                                <th className="border px-4 py-2">ID</th>
                                <th className="border px-4 py-2">Name</th>
                                <th className="border px-4 py-2">Email</th>
                                <th className="border px-4 py-2">Created At</th>
                            </tr>
                        </thead>
                        <tbody>
                            {users.map(user => (
                                <tr key={user.id}>
                                    <td className="border px-4 py-2">{user.id}</td>
                                    <td className="border px-4 py-2">{user.name}</td>
                                    <td className="border px-4 py-2">{user.email}</td>
                                    <td className="border px-4 py-2">{new Date(user.created_at).toLocaleString()}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </section>

            <section className="mb-6">
                <h2 className="text-2xl font-semibold mb-4">Disasters</h2>
                <div className="overflow-x-auto bg-white border border-gray-300 rounded-lg">
                    <table className="min-w-full">
                        <thead>
                            <tr className="bg-gray-200">
                                <th className="border px-4 py-2">ID</th>
                                <th className="border px-4 py-2">Type</th>
                                <th className="border px-4 py-2">Location</th>
                                <th className="border px-4 py-2">Date</th>
                                <th className="border px-4 py-2">Severity</th>
                            </tr>
                        </thead>
                        <tbody>
                            {disasters.map(disaster => (
                                <tr key={disaster.id}>
                                    <td className="border px-4 py-2">{disaster.id}</td>
                                    <td className="border px-4 py-2">{disaster.disaster_type}</td>
                                    <td className="border px-4 py-2">{disaster.location}</td>
                                    <td className="border px-4 py-2">{new Date(disaster.date).toLocaleDateString()}</td>
                                    <td className="border px-4 py-2">{disaster.severity}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </section>

            <section className="mb-6">
                <h2 className="text-2xl font-semibold mb-4">Predictions</h2>
                <div className="overflow-x-auto bg-white border border-gray-300 rounded-lg">
                    <table className="min-w-full">
                        <thead>
                            <tr className="bg-gray-200">
                                <th className="border px-4 py-2">ID</th>
                                <th className="border px-4 py-2">User ID</th>
                                <th className="border px-4 py-2">Disaster ID</th>
                                <th className="border px-4 py-2">Predicted Type</th>
                                <th className="border px-4 py-2">Date</th>
                            </tr>
                        </thead>
                        <tbody>
                            {predictions.map(prediction => (
                                <tr key={prediction.id}>
                                    <td className="border px-4 py-2">{prediction.id}</td>
                                    <td className="border px-4 py-2">{prediction.user_id}</td>
                                    <td className="border px-4 py-2">{prediction.disaster_id}</td>
                                    <td className="border px-4 py-2">{prediction.predicted_disaster_type}</td>
                                    <td className="border px-4 py-2">{new Date(prediction.prediction_date).toLocaleString()}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </section>

            <section className="mb-6">
                <h2 className="text-2xl font-semibold mb-4">Relief Resources</h2>
                <div className="overflow-x-auto bg-white border border-gray-300 rounded-lg">
                    <table className="min-w-full">
                        <thead>
                            <tr className="bg-gray-200">
                                <th className="border px-4 py-2">ID</th>
                                <th className="border px-4 py-2">Resource Name</th>
                                <th className="border px-4 py-2">Quantity</th>
                                <th className="border px-4 py-2">Location</th>
                                <th className="border px-4 py-2">Created At</th>
                            </tr>
                        </thead>
                        <tbody>
                            {resources.map(resource => (
                                <tr key={resource.id}>
                                    <td className="border px-4 py-2">{resource.id}</td>
                                    <td className="border px-4 py-2">{resource.resource_name}</td>
                                    <td className="border px-4 py-2">{resource.quantity}</td>
                                    <td className="border px-4 py-2">{resource.location}</td>
                                    <td className="border px-4 py-2">{new Date(resource.created_at).toLocaleString()}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </section>

            <section className="mb-6">
                <h2 className="text-2xl font-semibold mb-4">Disaster Trends</h2>
                <Line
                    data={{
                        labels: labels,
                        datasets: [
                            {
                                label: 'Number of Disasters',
                                data: data,
                                borderColor: 'rgba(75,192,192,1)',
                                backgroundColor: 'rgba(75,192,192,0.4)',
                                fill: true,
                            },
                        ],
                    }}
                    options={{
                        scales: {
                            y: {
                                beginAtZero: true,
                            },
                        },
                    }}
                />
            </section>
        </div>
    );
};

export default Dashboard;
